package kwon;

public class Code01 {
	
	static int num;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int anotherNum = 5;
		num = 2;
		
		System.out.println(num + anotherNum);//7
		System.out.println("Num : " + num);//Num : 2
		System.out.println("AnotherNum : " + anotherNum);//AnotherNum : 5
		System.out.println("sum : " + num + anotherNum);//sum : 25

	}

}
